class MainContainer extends React.Component {
  constructor(props) {
    super(props)
    this.heading = props.heading;
    this.className = props.className;
    this.children = props.children;
  }



  render() {
    return (
      <div className={this.className}>
        {this.heading}
        {this.children}
      </div>
    )
  }
}

class CardSmall extends React.Component {
  constructor(props) {
    super(props)
    this.className = props.className;
    this.heading = props.heading;
    this.time = props.time;
    this.building = props.building;
    this.cta = props.cta;
  }


  render() {
    return (
      <MainContainer heading='This is the heading' className={this.className}>
        <article className="card__wrapper">
          <h2 className="card__heading">{this.heading}</h2>
          <section className="card__details">
          <h2 className="details__primary">{this.time}</h2>
          <h2 className="details__secondary">{this.building}</h2>
          </section>
          <h2>{this.cta}</h2>
        </article>
      </MainContainer>
    )
  }
}

function allSmallCards() {
  let allCourses = courses.map((course) => <CardSmall key={course.id} className={course.className} heading={course.heading} time={course.time} building={course.building} cta="CTA" />)

  return allCourses
}




export default allSmallCards
