import React from 'react';
import courses from './courses';


class CardSmall extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        time: props.time,
        building: props.building,
        cta: props.cta
      }
      this.className = props.className;
      this.heading = props.heading;
      this.cta = props.cta;
      this.time = props.time;
      this.building = props.building;
    }

    componentDidMount() {
      this.setState({
        cta: this.state.cta
      })
    }


    render() {
      if (this.className === 'course') {
        this.containerClass = 'course__wrapper';
        return (
          <section className={`card ${this.className}`}>
            <article className="card__wrapper">
              <h2 className="card__heading">{this.heading}</h2>
              <section className="card__details">
                <h2 className="details__primary">{this.time}</h2>
                <h2 className="details__secondary">{this.building}</h2>
              </section>
              <h2 className="cta">{this.state.cta}</h2>
            </article>
          </section>
        )
      } else if (this.className === 'hold' || this.className === 'to-do') {
        return (
          <section className={`card ${this.className}`}>
            <article className="card__wrapper">
              <h2 className="card__heading">{this.heading}</h2>
              <section className="card__details">
                <h2 className="details__primary">{this.building}</h2>
              </section>
              <h2 className="cta">{this.state.cta}</h2>
            </article>
          </section>
        )
      }
    }
}

function AllCards() {
  return courses.map((course) => <CardSmall key={course.id} className={course.className} heading={course.heading} time={course.time} building={course.building} cta="CTA"/>)
}

export default AllCards
