// import logo from './logo.svg';
import React from 'react';
import './main-container.scss';



const courses = [
  {
    id: 'CG120',
    className: 'course',
    school: 'City College of New York',
    building: 'Comp Goeth cg122',
    Professor: 'Lynn Foster',
    heading: '2D Imaging and Illustration',
    time: 'th 6:15 pm – 9:05 pm',
    schedule: '1/25/19 – 05/22/19',
    career: 'Digital Design',
    credits: '3',
    status: 'enrolled',
    preReq: 'Typography 2',
    description: 'Interface design, information structuring, and interaction design for the Web. Sites will be examined from the perspective of design, utility, and interactivity. Design and development of HTML documents, CSS style sheets, text and images for websites including prototype testing. Students work individually and in teams, and will exit with an online portfolio.',
    booksNeeded: [
      'HTML5 in Easy Steps, Author: McGrath, Mike, Publisher: Computer Step, Edition: 2, Year Published: 2017, Price: 11.68 USD, Notes: As low as the displayed price',
      'HTML5 in Easy Steps, Author: McGrath, Mike, Publisher: Computer Step, Edition: 2, Year Published: 2017, Price: 11.68 USD, Notes: As low as the displayed price'
    ]
  },
  {
    id: 'CG1220',
    className: 'course',
    school: 'City College of New York',
    building: 'Financial Aid not paid',
    Professor: 'Lynn Foster',
    heading: 'Electronic Design 1',
    time: 'tu 6:15 pm – 9:05 pm',
    schedule: '1/25/19 – 05/22/19',
    career: 'Digital Design',
    credits: '3',
    status: 'enrolled',
    preReq: 'Typography 2',
    description: 'Interface design, information structuring, and interaction design for the Web. Sites will be examined from the perspective of design, utility, and interactivity. Design and development of HTML documents, CSS style sheets, text and images for websites including prototype testing. Students work individually and in teams, and will exit with an online portfolio.',
    booksNeeded: [
      'HTML5 in Easy Steps, Author: McGrath, Mike, Publisher: Computer Step, Edition: 2, Year Published: 2017, Price: 11.68 USD, Notes: As low as the displayed price',
      'HTML5 in Easy Steps, Author: McGrath, Mike, Publisher: Computer Step, Edition: 2, Year Published: 2017, Price: 11.68 USD, Notes: As low as the displayed price'
    ]
  },
  {
    id: 'CG1320',
    className: 'course',
    school: 'City College of New York',
    building: 'Comp Goeth cg121',
    Professor: 'Lynn Foster',
    heading: 'Programming for Artists',
    time: 'Mo 6:15 pm – 9:05 pm',
    schedule: '1/25/19 – 05/22/19',
    career: 'Digital Design',
    credits: '3',
    status: 'enrolled',
    preReq: 'Typography 2',
    description: 'Interface design, information structuring, and interaction design for the Web. Sites will be examined from the perspective of design, utility, and interactivity. Design and development of HTML documents, CSS style sheets, text and images for websites including prototype testing. Students work individually and in teams, and will exit with an online portfolio.',
    booksNeeded: [
      'HTML5 in Easy Steps, Author: McGrath, Mike, Publisher: Computer Step, Edition: 2, Year Published: 2017, Price: 11.68 USD, Notes: As low as the displayed price',
      'HTML5 in Easy Steps, Author: McGrath, Mike, Publisher: Computer Step, Edition: 2, Year Published: 2017, Price: 11.68 USD, Notes: As low as the displayed price'
    ]
  }
]


class MainContainer extends React.Component {
  constructor(props) {
    super(props)
    this.heading = props.heading;
    this.className = props.className;
    this.children = props.children;
  }

  render() {
    return (
      <div className='main__container'>
        <h2 className="main__container--heading">Courses: Spring 2019</h2>
        <AllSmallCards />
        {/* <AllLargeCards /> */}
      </div>
    )
  }
}

class CardSmall extends React.Component {
  constructor(props) {
    super(props)
    this.className = props.className;
    this.heading = props.heading;
    this.time = props.time;
    this.building = props.building;
    this.cta = props.cta;
  }


  render() {
    if (this.className === 'course') {
      this.containerClass = 'course__wrapper';
      return (
        <section className={`card ${this.className}`}>
          <article className="card__wrapper">
            <h2 className="card__heading">{this.heading}</h2>
            <section className="card__details">
              <h2 className="details__primary">{this.time}</h2>
              <h2 className="details__secondary">{this.building}</h2>
            </section>
            <h2 className="cta">{this.cta}</h2>
          </article>
        </section>
      )
    } else if (this.className === 'hold' || this.className === 'to-do') {
      return (
        <section className={`card ${this.className}`}>
          <article className="card__wrapper">
            <h2 className="card__heading">{this.heading}</h2>
            <section className="card__details">
              <h2 className="details__primary">{this.building}</h2>
            </section>
            <h2 className="cta">{this.cta}</h2>
          </article>
        </section>
      )
    }
  }
}

function AllSmallCards() {
  return courses.map((course) => <CardSmall key={course.id} className={course.className} heading={course.heading} time={course.time} building={course.building} cta="CTA"/>)
}

function AllLargeCards() {
  return courses.map((course) => <CardSmall key={course.id} className={course.className} heading={course.heading} time={course.time} building={course.building} cta="CTA"/>)
}




export default MainContainer
