import React from 'react';



export default class CardSmall extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        primary: props.primary,
        secondary: props.secondary,
        cta: props.cta
      }
      this.className = props.className;
      this.heading = props.heading;
      this.cta = props.cta;
      this.hideSecondary = this.hideSecondary.bind(this);
    }

    componentDidMount() {
      if(this.className === 'holds' || this.className === 'to-do') {
        this.setState({
          secondary: null
        })
      }

      this.setState({
        cta: this.state.cta
      })
    }

  hideSecondary() {
    if (this.state.secondary === null) {
      return null
    } else {
      return <h2 className="details__secondary">{this.state.secondary}</h2>
    }
  }


    render() {

      return (
          <section className={`card ${this.className}`}>
            <article className="card__wrapper">
              <h2 className="card__heading">{this.heading}</h2>
              <section className="card__details">
                <h2 className="details__primary">{this.state.primary}</h2>
                {this.hideSecondary}
              </section>
              <h2 className="cta">{this.state.cta}</h2>
            </article>
          </section>
      )
    }
}
