import React from 'react';
import courses from './courses';
import holds from './holds';
import CardSmall from './Card/CardSmall';

export default class AllCards extends React.Component {
    constructor(props) {
        super(props) 
        this.state = {
          cardName: props.cardName
        }
      } 

      componentDidMount() {
        if (<Courses />) {
          this.setState({
            cardName:`Courses: ${courses.term}`
          })
        }
        else if (<Holds />) {
          this.setState({
            cardName:`Holds`
          })
        }
        else if (<To-Do />) {
          this.setState({
            cardName:`To-Do`
          })
        }
      }

    render() {
        return (
            <div className='card__master'>
                <CardName cardName={this.state.cardName}/>
            </div>
        )
    }
}

class CardName extends React.Component {
    constructor(props) {
      super(props) 
      this.cardName = props.cardName
    } 

    render() {
      return (
        <h2 className="card__master--heading">{this.cardName}</h2>
      )
    }
  }

function Courses() {
    return courses.map((course) => <CardSmall key={course.id} className={course.className} heading={course.heading} primary={course.time} secondary={course.building} cta="CTA"/>)
  }

  function Holds() {
    return holds.map((hold) => <CardSmall key={hold.id} className={hold.className} heading={hold.heading} primary={hold.time} cta="CTA"/>)
  }
